Properties.Octopus.Action.Script.ScriptBody:
Write-Host "**********"
Write-Host "Chained Deployment of '$Chain_ProjectName' version '$Chain_ReleaseNum' to '$Chain_DeployTo'."
switch ($Chain_CreateOption) {
    "Try" { Write-Host " Release will be created if it doesn't exist." }
    "Create" { Write-Host " Release will be created before deploying." }
    "NoCreate" { Write-Host " Release will not be created and should already exist." }
}
Write-Host "**********"

$baseUri = $OctopusParameters['Octopus.Web.BaseUrl']
$reqheaders = @{"X-Octopus-ApiKey" = $Chain_ApiKey }

# Find Environment
$environments = Invoke-WebRequest "$baseUri/api/environments/all" -Headers $reqheaders -UseBasicParsing | ConvertFrom-Json
$environment = $environments | Where-Object {$_.Name -eq $Chain_DeployTo }
if ($environment -eq $null) {
    throw "Environment $Chain_DeployTo not found."
}

# Find Project
$projects = Invoke-WebRequest "$baseUri/api/projects/all" -Headers $reqheaders -UseBasicParsing | ConvertFrom-Json
$project = $projects | Where-Object {$_.Name -eq $Chain_ProjectName }
if ($project -eq $null) {
    throw "Project $Chain_ProjectName not found."
}

# Check for existing release with given release number
$releaseUri = "$baseUri$($project.Links.Self)/releases/$Chain_ReleaseNum"
try {
$release = Invoke-WebRequest $releaseUri -Headers $reqheaders -UseBasicParsing | ConvertFrom-Json
} catch {
    if ($_.Exception.Response.StatusCode.Value__ -ne 404) {
        $result = $_.Exception.Response.GetResponseStream()
        $reader = New-Object System.Io.StreamReader($result);
        $responseBody = $reader.ReadToEnd();
        throw "Error occurred retrieving a Release: $responseBody"
    }
}

# Create Release if needed
if ($release -eq $null -and $Chain_CreateOption -eq "NoCreate") {
    # release doesn't exist and we're not allowed to create it
    throw "Release $Chain_ReleaseNum of $Chain_ProjectName not found and the creation behaviour is set to never create."
} elseif ($release -ne $null -and $Chain_CreateOption -eq "Create") {
    # Release exists and we're being told to create it
    throw "Release $Chain_ReleaseNum of $Chain_ProjectName already exists and the creation behaviour is set to always create."
} elseif ($release -eq $null) {
    try {
        Write-Host "Creating Release version '$Chain_ReleaseNum' of '$Chain_ProjectName'"
        $releaseBody =  @{ Projectid = $Project.Id
            version = $Chain_ReleaseNum } | ConvertTo-Json
        $release = Invoke-WebRequest -Uri "$baseUri/api/releases" -Method Post -Headers $reqheaders -Body $releaseBody -UseBasicParsing | ConvertFrom-Json
    } catch {
        $result = $_.Exception.Response.GetResponseStream()
        $reader = New-Object System.Io.StreamReader($result);
        $responseBody = $reader.ReadToEnd();
        throw "Error occurred creating a Release: $responseBody"
    }
}

# We should have a release at this point
if ($release -eq $null) {
    throw "Release $Chain_ReleaseNum of $Chain_ProjectName not found. Could not create deployment."
}

# Create deployment
$deployment = @{
    ReleaseId = $release.Id
    EnvironmentId = $environment.Id
} | ConvertTo-Json
try {
    Write-Host "Deploying '$Chain_ProjectName' version '$Chain_ReleaseNum' to '$Chain_DeployTo'"
    $result = Invoke-WebRequest "$baseUri/api/deployments" -Method Post -Headers $reqHeaders -Body $deployment -UseBasicParsing | ConvertFrom-Json
} catch {
    $result = $_.Exception.Response.GetResponseStream()
    $reader = New-Object System.Io.StreamReader($result);
    $responseBody = $reader.ReadToEnd();
    throw "Error occurred creating a Deployment: $responseBody"
}
