Properties.Octopus.Action.Script.ScriptBody:
# Version date: 16th July, 2015
$ErrorActionPreference = 'Stop'
$VerbosePreference = 'Continue'

# Set process level FUR environment
$env:REDGATE_FUR_ENVIRONMENT = "Octopus Step Templates"

# Check if DLM Automation is installed.
$sqlReleaseModule = Get-Module -ListAvailable -Name SQLRelease
if ($sqlReleaseModule -eq $null) { 
    throw "Cannot find DLM Automation on your Octopus Tentacle. If DLM Automation is installed, try restarting the Tentacle service for it to be detected."
}
$currentVersion = $sqlReleaseModule.Version
$minimumRequiredVersion = [version] '1.2.1.3109'
if ($currentVersion -lt $minimumRequiredVersion) { 
    throw "This step requires DLM Automation version $minimumRequiredVersion or later. The current version is $currentVersion. The latest version can be found at http://www.red-gate.com/dlmas/download"
}

# Check the parameters.
if ([string]::IsNullOrWhiteSpace($SQLReleaseDeploymentResourcesPath)) { throw "You must enter a value for 'Export path'." }
if ([string]::IsNullOrWhiteSpace($SQLReleaseDatabaseName)) { throw "You must enter a value for 'Target database name'." }
if ([string]::IsNullOrWhiteSpace($SQLReleaseDatabaseServer)) { throw "You must enter a value for 'Target SQL Server instance'." } 
if ([string]::IsNullOrWhiteSpace($SQLReleaseNuGetDbPackageDownloadStepName)) { throw "You must enter a value for 'Database package step'." } 

$queryBatchTimeout = 30
if (![string]::IsNullOrWhiteSpace($SQLReleaseQueryBatchTimeout)) {
    if (![int32]::TryParse($SQLReleaseQueryBatchTimeout , [ref]$queryBatchTimeout )) {
        throw 'The query batch timeout must be a numerical value (in seconds).'
    }
    if ($queryBatchTimeout -lt 0) {
        throw "The query batch timeout can't be negative."
    }
}

# Check whether database deployment resources export path exists and is a valid directory path 
if((Test-Path $SQLReleaseDeploymentResourcesPath) -eq $true) {
    if((Get-Item $SQLReleaseDeploymentResourcesPath) -isnot [System.IO.DirectoryInfo]) {
        throw "The export path is not a valid folder: $SQLReleaseDeploymentResourcesPath"
    }
} else {
    throw "The export path folder doesn't exist, or the current Windows account can't access it: $SQLReleaseDeploymentResourcesPath"
}

# Get the NuGet package ID and validate the step name.
$nugetPackageId = $OctopusParameters["Octopus.Action[$SQLReleaseNuGetDbPackageDownloadStepName].Package.NuGetPackageId"]
if ($nugetPackageId -eq $null) { 
    throw "The 'Database package download step' is not a 'Deploy a NuGet package' step: '$SQLReleaseNuGetDbPackageDownloadStepName'"
}

# Constructing the unique export path.
$projectId = $OctopusParameters['Octopus.Project.Id']
$releaseNumber = $OctopusParameters['Octopus.Release.Number']
$exportPath = Join-Path (Join-Path (Join-Path $SQLReleaseDeploymentResourcesPath $projectId) $releaseNumber) $nugetPackageId

# Create and test connection to the database.
$databaseConnection = New-DlmDatabaseConnection -ServerInstance $SQLReleaseDatabaseServer `
                                                -Database $SQLReleaseDatabaseName `
                                                -Username $SQLReleaseDatabaseUsername `
                                                -Password $SQLReleaseDatabasePassword | Test-DlmDatabaseConnection

# Import and deploy the release.
Import-DlmDatabaseRelease $exportPath | Use-DlmDatabaseRelease -DeployTo $databaseConnection -QueryBatchTimeout $queryBatchTimeout
