Properties.Octopus.Action.Script.ScriptBody:
# Version date: 16th July, 2015
$ErrorActionPreference = 'Stop'
$VerbosePreference = 'Continue'

# Set process level FUR environment
$env:REDGATE_FUR_ENVIRONMENT = "Octopus Step Templates"

# Check if DLM Automation is installed.
$sqlReleaseModule = Get-Module -ListAvailable -Name SQLRelease
if ($sqlReleaseModule -eq $null) { 
    throw "Cannot find DLM Automation on your Octopus Tentacle. If DLM Automation is installed, try restarting the Tentacle service for it to be detected."
}
$currentVersion = $sqlReleaseModule.Version
$minimumRequiredVersion = [version] '1.2.1.3109'
if ($currentVersion -lt $minimumRequiredVersion) { 
    throw "This step requires DLM Automation version $minimumRequiredVersion or later. The current version is $currentVersion. The latest version can be found at http://www.red-gate.com/dlmas/download"
}

# Check the parameters.
if ([string]::IsNullOrWhiteSpace($SQLReleaseSourceDatabaseName)) { throw "You must enter a value for 'Source database name'." }
if ([string]::IsNullOrWhiteSpace($SQLReleaseSourceDatabaseServer)) { throw "You must enter a value for 'Source SQL Server instance'." }
if ([string]::IsNullOrWhiteSpace($SQLReleaseTargetDatabaseName)) { throw "You must enter a value for 'Target database name'." }
if ([string]::IsNullOrWhiteSpace($SQLReleaseTargetDatabaseServer)) { throw "You must enter a value for 'Target SQL Server instance'." }
if ([string]::IsNullOrWhiteSpace($SQLReleaseFilterPath)) { $SQLReleaseFilterPath = $null } 
if ([string]::IsNullOrWhiteSpace($SQLReleaseCompareOptions)) { $SQLReleaseCompareOptions = $null }

$queryBatchTimeout = 30
if (![string]::IsNullOrWhiteSpace($SQLReleaseQueryBatchTimeout)) {
    if (![int32]::TryParse($SQLReleaseQueryBatchTimeout , [ref]$queryBatchTimeout )) {
        throw 'The query batch timeout must be a numerical value (in seconds).'
    }
    if ($queryBatchTimeout -lt 0) {
        throw "The query batch timeout can't be negative."
    }
}

$targetDB = New-DlmDatabaseConnection -ServerInstance $SQLReleaseTargetDatabaseServer -Database $SQLReleaseTargetDatabaseName -Username $SQLReleaseTargetUsername -Password $SQLReleaseTargetPassword | Test-DlmDatabaseConnection
$sourceDB = New-DlmDatabaseConnection -ServerInstance $SQLReleaseSourceDatabaseServer -Database $SQLReleaseSourceDatabaseName -Username $SQLReleaseSourceUsername -Password $SQLReleaseSourcePassword | Test-DlmDatabaseConnection

# Create the deployment resources, only adding the arguments that are not null or empty.
$release = New-DlmDatabaseRelease -Target $targetDB `
                                  -Source $sourceDB `
                                  -TransactionIsolationLevel $SQLReleaseTransactionIsolationLevel `
                                  -FilterPath $SQLReleaseFilterPath `
                                  -SQLCompareOptions $SQLReleaseCompareOptions

# Deploy the source schema to the target database.
$release | Use-DlmDatabaseRelease -DeployTo $targetDB -SkipPreUpdateSchemaCheck -QueryBatchTimeout $queryBatchTimeout
